struct inode_header { 
atomic_t 
i_count; // Reference count 
spinlock_t 
i_lock; 
inode_data 
* data; 
// Data object 
// Other objects 
address_space i_data; // Cached pages 
tx_data xobj; 
// for conflict detection 
list i_sb_list; // kernel bookkeeping 
}; 

struct inode_data { 
inode_header * header; 
// Common inode data fields 
unsigned long i_ino; 
loff_t 
i_size; // etc. 
};
