PERM_ENTRY lookup(addr_t addr) { 
PERM_ENTRY e = root[addr >> 22]; 
if(is_tbl_ptr(e)) { 
PERM_TABLE * mid = e<<2; 
e = mid[(addr >> 12) & 0x3FF]; 
if(is_tbl_ptr(e)) { 
PERM_TABLE * leaf = e<<2; 
e = leaf[(addr >> 6) & 0x3F]; 
} 
} 
return e; 
} 

